package guviTask9;

public class Pattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int n = 4; 
	     int num = 1; 
	        for (int i = 0; i < n; i++) 
	        {
	            for (int j = 0; j <= i; j++)
	            {
	                System.out.print(num + " "); 
	                num++;
	        }
	            System.out.println();
	        }

	}

}
